import socket
import webbrowser
import maya.cmds as cmds
import maya.mel as mel

#turn off autokey
cmds.autoKeyframe(state=False)

#query ip address
def getIP():
    ip = socket.gethostbyname(socket.gethostname())
    return ip

#create commandPort
def createCommandPort(ip,port):
    cport = ip + ':' + str(port)
    if(cmds.commandPort (cport, q=True)==1):
        closeCommandPort(ip,port)
    else:
        cmds.commandPort (name=cport, stp='python', eo=True)
        cmds.headsUpMessage('CommandPort established at ' + cport)

def closeCommandPort(ip,port):
    cport = ip + ':' + port
    cmds.commandPort (name=cport, close=True)
    cmds.headsUpMessage('CommandPort closed at ' + cport)

#set headsUpMessage
def headsUp():
    cmds.headsUpMessage('connected to AirSketch:Maya')

#create default object to draw with
def createLoc():
    cmds.spaceLocator(name='drawLoc')
    cmds.aimConstraint('persp','drawLoc',aim=[0,0,1])
    cmds.delete('drawLoc_aimConstraint1')
    cmds.delete('newCurve')
    cmds.delete('rebuildCurve1')

#set attributes of selected object to record
def recordMotion():
    cmds.select('drawLoc')
    cmds.recordAttr(at=['translateX','translateY','translateZ'])
    cmds.playbackOptions(min=1,max=100)
    cmds.play(rec=True)

#stop playback
def stopRecord():
    cmds.play(state=False)
    createCurve()

#get node
def getNode(nodeType):
    nodeList = cmds.ls(et=nodeType, tr=True)
    node = nodeList[-1]
    return node

def getParentNode(node):
    parentNodeList = cmds.listRelatives(node, p=True)
    parentNode = parentNodeList[-1]
    return parentNode

#create curve from object
def createCurve():
    #delete recording and create motion trail
    cmds.currentTime(1)
    cmds.select('drawLoc')
    cmds.recordAttr(delete=True)
    maxTime = cmds.playbackOptions(query=True,max=True)
    cmds.snapshot(mt=True, et=maxTime)

    #get number of keys
    keys = cmds.keyframe('drawLoc', attribute='translateX', query=True, keyframeCount=True )

    #select current motion trail
    trail = getNode('motionTrail')
    cmds.select(trail)

    #get points from motion trail
    points = cmds.getAttr(trail+'.pts')

    #build curve from points
    cmds.curve(pw=points)

    #delete locator and motion trail
    cmds.delete(trail)
    cmds.delete('drawLoc')

    #rebuild curve
    curve = getParentNode(getNode('nurbsCurve')) 
    cmds.select(curve)
    cmds.rebuildCurve(s=100,n='newCurve',ch=True, rpo=False)
    
    #select curve
    curve = getParentNode(getNode('nurbsCurve'))    
    cmds.select(curve)
    
    #blue neon brush
    blueNeon()
    
    #attach current brush to curve (default if none selected)
    cmds.AttachBrushToCurves()
    
    #select curve again for editing
    cmds.select(curve)
    
def simplifyCurve(stepValue):
    spans = int(stepValue*10)
    cmds.setAttr('rebuildCurve1.spans',spans)
    
