#window class
class AirWindow:
    def __init__(self):
        self.window = 'airWindow'
        self.title = 'AirSketch:Maya Control Panel'
        self.size = (500,200)
    def connectButtonCommand(self, *args):
        ip=cmds.textFieldGrp(self.ipAddressText, q=True, tx=True)
        port=cmds.textFieldGrp(self.portNumberText, q=True, tx=True)
        createCommandPort(ip,port)
    def disconnectButtonCommand(self, *args):
        createCommandPort()
    def helpButtonCommand(self, *args):
        url = 'http://germantownstudios.com'
        webbrowser.open(url, new=2)
    def commonButtons(self):
        self.commonButtonSize = (100, 50)
        self.ipAddressText=cmds.textFieldGrp(
            label='ip address: ',
            text=getIP(),
            editable=True)
        self.portNumberText=cmds.textFieldGrp(
            label='port: ',
            text='8888',
            editable=True)
        self.divider = cmds.separator(
            height=40,
            style='out')
        self.connectButton = cmds.button(
            label='connect',
            height=self.commonButtonSize[1],
            command=self.connectButtonCommand)
        self.helpButton = cmds.button(
            label='help',
            height=self.commonButtonSize[1],
            command=self.helpButtonCommand)
        cmds.formLayout(
            self.mainForm, e=True,
            attachForm=(
                [self.ipAddressText,'left',2],
                [self.ipAddressText,'top',10],
                [self.portNumberText,'left',2],
                [self.divider,'left',5],
                [self.divider,'right',5],
                [self.connectButton,'left',10],
                [self.connectButton,'bottom',10],
                [self.helpButton,'bottom',10],
                [self.helpButton,'right',10]
            ),
            attachPosition=(
                [self.connectButton,'right',1,33],
                [self.helpButton,'left',0,67]
            ),
            attachControl=(
                [self.portNumberText,'top',5,self.ipAddressText],
                [self.divider,'bottom',1,self.connectButton]
            ),
            attachNone=(
                    [self.connectButton,'top'],
                    [self.helpButton,'top']
            )
        )
    def create(self):
            if (cmds.window(self.window, exists=True)):
                cmds.deleteUI(self.window, window=True)
            self.window = cmds.window(self.window, title=self.title, widthHeight=self.size, sizeable=False)
            self.mainForm = cmds.formLayout(nd=100)
            self.commonButtons()
            cmds.showWindow()